import React from 'react';
import {connect} from 'react-redux';
import * as transactionActions from '../actions/transactionActions';
import {bindActionCreators} from 'redux';
import UsersInCore from '../components/UsersInCore';
import UserDropdown from '../components/UserDropdown';
import { Container, Row, Col, Card } from 'reactstrap';
import UserIsAlreadyInCore from '../components/UserIsAlreadyInCore';
import UserValidNewCoreEntry from '../components/UserValidNewCoreEntry';
import * as transactionManipulations from '../api/transactionManipulations';


class ExitCore extends React.Component {
    constructor(props, context) {
        super(props, context);
        this.state = {
            selectedUser:'',
            selectedUserIsInCore:false,
            selectedUsersEntryTransactionID:''
        };
        this.updateSelection = this.updateSelection.bind(this);

    }   

    updateSelection(event) {
        this.setState({
            selectedUser:event.target.value
        });
        this.isSelectedUserInCore(event.target.value);
    }

    render() {
        let knownInitials = transactionManipulations.usersInCore;
        return(
            <div>
                <Container>
                    <Row>
                        <Col>
                            <Row>
                                    <h2>Select Person to Enter the Core</h2>
                                    <Row>
                                        <Col md="2"></Col>
                                        <Col md="8">
                                            <UserDropdown knownInitials = {knownInitials} updateSelection = {this.updateSelection}/>
                                        </Col>
                                        <Col md="2"></Col>
                                    </Row>
                            </Row>
                        </Col>
                        <Col>
                            <Card>
                                <h2>People Currently in Core</h2>
                                <Row>
                                    <Col md="1"></Col>
                                    <Col md="10">
                                    <UsersInCore transactions = {this.props.transactions} />
                                    </Col>
                                    <Col md="1"></Col>
                                </Row>
                            </Card>
                        </Col>
                    </Row>
                </Container>


            </div>
        );
    }
}

function mapStateToProps(state, ownProps){
    return {
        transactions:state.transactions,
        users:state.users,
        timeBoundry:state.timeBoundry
    };
}

function mapDispatchToProps(dispatch) {
    return {
        transactionActions: bindActionCreators(transactionActions, dispatch)
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ExitCore);
